import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";

const Navbar = ({ isLoggedIn, setIsLoggedIn }) => {
  const [darkMode, setDarkMode] = useState(false);
  const history = useHistory();

  useEffect(() => {
    if (darkMode) {
      document.body.classList.add("dark");
    } else {
      document.body.classList.remove("dark");
    }
  }, [darkMode]);

  const handleLoginLogout = () => {
    if (isLoggedIn) {
      setIsLoggedIn(false);
    } else {
      history.push("/login");
    }
  };

  return (
    <nav className="bg-white shadow h-14 flex items-center justify-between px-4">
      <div className="flex items-center">
        <img src="/applog.png" alt="Logo" className="h-10 mr-4" />
        <div className="text-xl font-bold">MyApp</div>
      </div>
      <button
        onClick={() => setDarkMode(!darkMode)}
        className="ml-4 p-2 bg-gray-200 rounded hover:bg-gray-300"
      >
        {darkMode ? "Light Mode" : "Dark Mode"}
      </button>
      <ul className="flex space-x-4">
        <li>
          <Link to="#workspace" className="text-gray-700 hover:text-gray-900">
            Workspace
          </Link>
        </li>
        <li>
          <Link to="#about" className="text-gray-700 hover:text-gray-900">
            About
          </Link>
        </li>
        <li>
          <Link to="#contact" className="text-gray-700 hover:text-gray-900">
            Contact
          </Link>
        </li>
        <li>
          <button
            onClick={handleLoginLogout}
            className="text-gray-700 hover:text-gray-900"
          >
            {isLoggedIn ? "Log Out" : "Log In"}
          </button>
        </li>
      </ul>
    </nav>
  );
};

export default Navbar;
